import React from "react";

function Math (props) {
    let total;
    switch(props.operator) {
        case "+":
            total = props.num1 + props.num2;
            break;
        case "-":
            total = props.num1 - props.num2;
            break;
       case "*":
            total = props.num1 * props.num2;
            break;
        case "/":
            total = props.num1 / props.num2;
            break;
        default:
            total = "Not a Number";
    }     
}

export default Math;
